import React, {useState, useEffect} from 'react';
import './App.css';
import { createStore } from 'redux'
import {Line} from "react-chartjs-2";
import BarChartD3 from './BarChartD3.js'
import GeoMap1 from './GeoMap1.js'
import ThreeAnim1 from './ThreeAnim1.js'

let defaultState = {
  chartValues:[]
}
const MAX_CHART_VALS = 10//600//10 minutes

function App() {

  const [chartValues, updateChartValues] = useState([])
  const [currentMessage, updateCurrentMessage] = useState([])
  const [messages, updateMessages] = useState([])
  const store = createStore(reducer)
  function reducer(state=defaultState, action) {
    switch (action.type) {
      case 'UPDATE_CHART':
        let chartValuesTemp = state.chartValues.concat([])
        chartValuesTemp.push(action.value)
        if(chartValuesTemp.length > MAX_CHART_VALS){
          chartValuesTemp.shift()
        }
        return {...state,chartValues:chartValuesTemp}
      default:
        return state
    }
  }

  useEffect(()=>{


    function find_max_sum(arr){
      let currentItem = 0
      let lastItem = 0
      let sum = 0
      arr.forEach((item, index)=>{
        if(index===0){
          currentItem = item;
          sum += currentItem
          lastItem = item
        }else if(item > lastItem){
          currentItem = item;
          sum -= lastItem
        }
      })
      console.log(sum)
    }
    find_max_sum([1,2,3,4,1,10])


    //figurePi for Eargo//
    // function figurePi(sideLength){
    //   let approxPi = 0
    //   let radius = sideLength/2
    //   let inside = 0
    //   let total = 0
    //   for(let i=0;i<sideLength;i+=1){
    //     for(let j=0;j<sideLength;j+=1){
    //       let distanceToPoint = Math.floor(Math.sqrt(((i/2)*(i/2))+((j/2)*(j/2))))
    //       if(distanceToPoint < radius){
    //         inside +=1
    //       }
    //       total+=1
    //     }
    //   }
    //   let ratio = inside/total
    //   let areaOfSquare = sideLength * sideLength
    //   let areaOfCircle = areaOfSquare * ratio
    //   approxPi = areaOfCircle/(radius*radius)
    //   console.log('approxPi:', approxPi)
    //
    // }
    // let pi = figurePi(100000)
    //100 iterations approxPi: 3.1796
    //1000 iterations approxPi: 3.14552
    //10000 approxPi: 3.14199016
    //100000 approxPi: 3.1416325412

    // function fib(max){
    //   let arr = [1,1]
    //   // let acc = 0
    //   // while(acc<max){
    //   //   acc = arr[arr.length-1]+arr[arr.length-2]
    //   //   console.log('acc:', acc)
    //   //   arr.push(acc)
    //   // }
    //   console.log('arr:', arr)
    // }
    // fib(50)


    messages.push({user:'system', message:'how can I help you'})

    store.subscribe(() => {
      let state = store.getState()
      updateChartValues(state.chartValues)
    })
    setInterval(()=>{
      store.dispatch({type:'UPDATE_CHART', value: Math.floor(Math.random() * 100)})
    },1000)

  },[])

  function formatTime(value){
    if(value < 10){
      return '0'+value
    }
    return value
  }

  let labels = []
  function makeLabel(timestamp){
    return formatTime(timestamp.getHours())+':'+formatTime(timestamp.getMinutes())+':'+formatTime(timestamp.getSeconds())
  }

  function makeLabels(){
    let currentSecond = new Date(Date.now())
    for(let i=chartValues.length-1;i>=0;i-=1){
      labels.push(makeLabel(new Date(currentSecond - i * 1000)))
    }
  }
  makeLabels()
  let total = chartValues.reduce((accum,curr)=>{return accum+curr},0)
  let average = total/chartValues.length

  let data = {
    labels: labels,
    datasets: [
      {
        label: "Spam messages received per second",
        fillColor: "rgba(220,220,220,0.2)",
        lineTension:0.1,
        strokeColor: "rgba(220,220,220,1)",
        pointColor: "rgba(220,220,220,1)",
        pointStrokeColor: "#fff",
        pointHighlightFill: "#fff",
        pointHighlightStroke: "rgba(220,220,220,1)",
        data: chartValues
      }
    ]
  };
  let chartOptions={
    animation:{
      duration: 0
    }
  }

  function onMessageChange(evt){
    console.log('evt.target.value:', evt.target.value)
    updateCurrentMessage(evt.target.value)
  }

  function onClickSend(evt){
    console.log('currentMessage:', currentMessage)
    console.log('messages:', messages)
    updateCurrentMessage('')
    messages.push({user:'me', message:currentMessage})
  }

  let displayMessages = messages.map((msg)=>{
    return <div className={msg.user}>{msg.message}</div>
  })

  //let messages = [];


  return (
    <div className="App">
      <h4>Spaminator</h4>
      <h5>The Enterprise Spam Real Time Monitor - Displaying Your Spam By The Second</h5>
      <div className='lineChartContainer'>
        <Line data={data || {}} options={chartOptions} />
      </div>

      <div>{'Spam messages received just this last second:'}</div>
      <div>{chartValues[chartValues.length-1]+' at '+new Date(Date.now()).toLocaleString()}</div>
      <div>{'Average number of spam messages per second:'}</div>
      <div>{Math.floor(average)}</div>
      <div className={'d3BarChartContainer'}>
        <BarChartD3 data={chartValues} size={[400, 300]} />
      </div>
      <div className={'threeContainer'}>
        <ThreeAnim1/>
      </div>
      <div className={'changeContainer'}>
        <div className={'changeHeader'}>
          <span>Jordi's Bot</span>
          <span>How can I help you?</span>
        </div>
        <div className={'changeContent'}>
          {displayMessages}
        </div>
        <div className={'changeFooter'}>
          <input width={150} className={'messageInput'} type={'text'} value={currentMessage} onChange={(evt)=>{onMessageChange(evt)}}/>
          <button onClick={(evt)=>{onClickSend(evt)}}>send</button>
        </div>

      </div>
      <div className={'mapContainer'}>
        <GeoMap1/>
      </div>

    </div>
  );
}

export default App;
