import React, {useEffect, useState} from 'react'
import {withFauxDOM} from 'react-faux-dom'
import * as d3 from 'd3'
import {makeStyles} from '@material-ui/core/styles'

const styles = makeStyles((theme)=>({
  poobar:{
    marginTop:'20px'
  }
}))

function BarChartD3(props){
  const faux = props.connectFauxDOM('div','chart')
  let [svg, setSvg] = useState(d3.select(faux).append('svg').attr('width', 400).attr('height',300))

  useEffect(()=>{
      d3.select(faux)
          .append('div')
          .html('Hello World!')
      props.animateFauxDOM(800)
  },[])


  return (
      <div>
        <h2>Here is some fancy data:</h2>
        <div className='renderedD3'>
          {props.chart}
        </div>
      </div>
  )

}
export default withFauxDOM(BarChartD3)
