import React, {useEffect, useRef, useState} from 'react'
import './ThreeAnim1.css'
import * as THREE from "three";
import {makeStyles} from '@material-ui/core/styles'

const styles = makeStyles((theme)=>({
  poobar:{
    marginTop:'20px',
  }
}))

function ThreeAnim1(props){
  //let [svg, setSvg] = useState()
  const threeContainer = useRef(null);
  //const data = [1,2,3,4,5,6,7,8,9]
  useEffect(()=>{
    if(threeContainer && threeContainer.current){
      let scene = new THREE.Scene();
      let camera = new THREE.PerspectiveCamera( 75, window.innerWidth/window.innerHeight, 0.1, 1000 );
      let renderer = new THREE.WebGLRenderer();
      //renderer.setSize( window.innerWidth, window.innerHeight );
      renderer.setSize( 200, 200 );
      // document.body.appendChild( renderer.domElement );
      // use ref as a mount point of the Three.js scene instead of the document.body
      console.log('threeContainer:', threeContainer)
      threeContainer.current.appendChild( renderer.domElement );
      let geometry = new THREE.BoxGeometry( 1, 1, 1 );
      // let material = new THREE.MeshBasicMaterial( { color: 0x00ff00 } );
      // let cube = new THREE.Mesh( geometry, material );


      //let geometry = new THREE.CubeGeometry(200, 200, 200);

      // Set random colours for each of the 6 faces :)
      let color1 = Math.random() * 0xffffff
      let color2 = Math.random() * 0xffffff
      let color3 = Math.random() * 0xffffff
      let color4 = Math.random() * 0xffffff
      let color5 = Math.random() * 0xffffff
      let color6 = Math.random() * 0xffffff


      geometry.faces[ 0 ].color.setHex( color1 );
      geometry.faces[ 1 ].color.setHex( color1 );
      geometry.faces[ 2 ].color.setHex( color2 );
      geometry.faces[ 3 ].color.setHex( color2 );
      geometry.faces[ 4 ].color.setHex( color3 );
      geometry.faces[ 5 ].color.setHex( color3 );
      geometry.faces[ 6 ].color.setHex( color4 );
      geometry.faces[ 7 ].color.setHex( color4 );
      geometry.faces[ 8 ].color.setHex( color5 );
      geometry.faces[ 9 ].color.setHex( color5 );
      geometry.faces[ 10 ].color.setHex( color6 );
      geometry.faces[ 11 ].color.setHex( color6 );

      let material = new THREE.MeshBasicMaterial( { vertexColors: THREE.FaceColors } );
      let numCubes = 6
      let cubes = [];
      let cube = new THREE.Mesh(geometry, material);
      //scene.add( cube );
      scene.add( cube );
      camera.position.z = 5;
      let radius = 2.5;



      let angle=0;



      let animate = function () {
        requestAnimationFrame( animate );
        cube.rotation.x += 0.01;
        cube.rotation.y += 0.01;
        cube.position.x = Math.sin(angle) * radius
        cube.position.y = Math.cos(angle) * radius
        angle += 0.01
        renderer.render( scene, camera );
      };
      animate();
    }


  },[props.data, threeContainer])


  return (
      <div className={'threeAnim1'} ref={threeContainer} id={'threeContainer1'}>
      </div>
  )

}
export default ThreeAnim1
