import React, {useState, useEffect} from 'react'
import './Component1.css'
import * as rr from './ReactiveRobot'
import * as ev from './Events'

function Component1() {
  const [currentValue, setCurrentValue] = useState(`Component1 inited at ${Date.now()}`)
  console.log('Component1==>rendering')
  useEffect(()=>{
    rr.addObserver(onEvent)
    return ()=>{rr.removeObserver(onEvent)}//cleanup on unmount
  },[])

  function onEvent(event){
    switch (event.type){
      case ev.GET_DATA:
        console.log('component1==>received GET_DATA==>rr.globalData:', rr.globalData)
        break
      case ev.COMPONENT1_TEST:
        console.log('component1==>received COMPONENT1_TEST')
        setCurrentValue(`${event.data.name} received at ${Date.now()}`)
    }
  }
  return (
    <div className="Component1">
      <span>Component1</span>
      <span>{currentValue}</span>
    </div>
  );
}

export default Component1;
