import React, {useState, useEffect} from 'react';
import logo from './logo.svg';
import './App.css';
import { createStore } from 'redux'
import {Line, Doughnut} from "react-chartjs-2";
const Chart = require('chart.js');

let defaultState = {
  chartValues:[]
}
const MAX_CHART_VALS = 600//10 minutes

function App() {

  const [chartValues, updateChartValues] = useState([])
  const [latestChartValue, updateLatestChartValue] = useState([])

  function reducer(state=defaultState, action) {
    switch (action.type) {
      case 'UPDATE_CHART':
        let chartValuesTemp = state.chartValues.concat([])
        chartValuesTemp.push(action.value)
        if(chartValuesTemp.length > MAX_CHART_VALS){
          chartValuesTemp.shift()
        }
        return {...state,chartValues:chartValuesTemp}
      default:
        return state
    }
  }

  useEffect(()=>{
    let store = createStore(reducer)
    store.subscribe(() => {
      let state = store.getState()
      updateChartValues(state.chartValues)
    })
    setInterval(()=>{
      store.dispatch({type:'UPDATE_CHART', value: Math.floor(Math.random() * 100)})
    },1000)
  },[])

  function formatTime(value){
    if(value < 10){
      return '0'+value
    }
    return value
  }

  let labels = []
  function makeLabel(timestamp){
    return formatTime(timestamp.getHours())+':'+formatTime(timestamp.getMinutes())+':'+formatTime(timestamp.getSeconds())
  }

  function makeLabels(){
    let currentSecond = new Date(Date.now())
    for(let i=chartValues.length-1;i>=0;i-=1){
      labels.push(makeLabel(new Date(currentSecond - i * 1000)))
    }
  }
  makeLabels()
//console.log('labels:', labels)

  let data = {
    labels: labels,
    datasets: [
      {
        label: "Spam messages received per second",
        fillColor: "rgba(220,220,220,0.2)",
        lineTension:0.1,
        strokeColor: "rgba(220,220,220,1)",
        pointColor: "rgba(220,220,220,1)",
        pointStrokeColor: "#fff",
        pointHighlightFill: "#fff",
        pointHighlightStroke: "rgba(220,220,220,1)",
        data: chartValues
      }
    ]
  };
  let chartOptions={
    animation:{
      duration: 0
    }
  }

  return (
    <div className="App">
      <h4>Spaminator</h4>
      <h5>The Enterprise Spam Real Time Monitor</h5>
      <Line data={data || {}} options={chartOptions} />
      <div>{'Spam messages received just this last second:'}</div>
      <div>{chartValues[chartValues.length-1]+' at '+new Date(Date.now()).toLocaleString()}</div>
    </div>
  );
}

export default App;
