import React, {useEffect, useRef, useState} from 'react'
import {withFauxDOM} from 'react-faux-dom'
import * as d3 from 'd3'
import {makeStyles} from '@material-ui/core/styles'

const styles = makeStyles((theme)=>({
  poobar:{
    marginTop:'20px'
  }
}))

function BarChartD3(props){
  const faux = props.connectFauxDOM('div','chart')
  //let [svg, setSvg] = useState()
  const d3Container = useRef(null);
  const data = [1,2,3,4,5,6,7,8,9]
  useEffect(()=>{
    //let svg = d3.select(faux).append('svg').attr('width', 400).attr('height',300)
    if(d3Container.current){
      console.log('I have d3Container.current==>props.data:', props.data)
      const svg = d3.select(d3Container.current).select('#d3Graph1')
     // const svg = d3.select(d3Container.current).append('g')//this keeps appending 'g' every time it renders
      //const svg = d3.select(d3Container.current)//if I don't append 'g' it renders once and never updates

        //svg.selectAll('rect').remove()//does nothing, elements still accumulate
        .selectAll('rect')
        //.exit().remove()
        .data(props.data)

      // Enter new D3 elements
          //this works, but keeps appending new elements whenever the data changes
        .enter()
        .append('rect')

        .attr('x', (d, i) => i * 25)
        .attr('width', 20)
        //.transition()// animates adding the elements, but they never change
        .attr('y', (d)=>{return 100-d})
        .attr('height', d => d)
        //console.log('rects:', rects)
         .exit().remove()//elements keep accumulating
        //.text((d) => d)
         //d3.selectAll('rect').exit().remove()//elements keep accumulating
        //rects.exit().remove()//elements keep accumulating

    }
    // let g = svg.append('g').attr('id','group1')
    // console.log('props:', props)
    // console.log('g:', g)
    // //d3.select('#group1')
    //   g.append('rect')
    //     .data([1,2,3,4,5,6,7,8,9])
    //   .attr('width', 10)
    //   .attr('height', 10)
    //   .attr('x', (d)=>{return d * 10})
    //   .style('fill','red')




    ///props.animateFauxDOM(800)
  },[data, d3Container.current])


  return (
      <div>
        <svg
          className="d3-component"
          width={400}
          height={200}
          ref={d3Container}
        ><g id='d3Graph1'></g></svg>
      </div>
  )

}
export default withFauxDOM(BarChartD3)
