import React, {useEffect, useRef, useState} from 'react'
import {withFauxDOM} from 'react-faux-dom'
import * as d3 from 'd3'
import {makeStyles} from '@material-ui/core/styles'

const styles = makeStyles((theme)=>({
  poobar:{
    marginTop:'20px'
  }
}))

function BarChartD3(props){
  const faux = props.connectFauxDOM('div','chart')
  //let [svg, setSvg] = useState()

  useEffect(()=>{
    let svg = d3.select(faux).append('svg').attr('width', 400).attr('height',300)
    let g = svg.append('g').attr('id','group1')
    console.log('props:', props)
    console.log('g:', g)
    //d3.select('#group1')
      g.append('rect')
        .data([1,2,3,4,5,6,7,8,9])
      .attr('width', 10)
      .attr('height', 10)
      .attr('x', (d)=>{return d * 10})
      .style('fill','red')




    props.animateFauxDOM(800)
  },[])


  return (
      <div>
        <h2>Here is some fancy data:</h2>
        <div className='renderedD3'>
          {props.chart}
        </div>
      </div>
  )

}
export default withFauxDOM(BarChartD3)
