import React, {useEffect, useRef, useState} from 'react'
import * as d3 from 'd3'
import './BarChartD3.css'
// import {makeStyles} from '@material-ui/core/styles'
//
// const styles = makeStyles((theme)=>({
//   poobar:{
//     marginTop:'20px',
//   }
// }))

function BarChartD3(props){
  //let [svg, setSvg] = useState()
  const d3Container = useRef(null);
  //const data = [1,2,3,4,5,6,7,8,9]
  useEffect(()=>{
    if(d3Container.current){
      //console.log('I have d3Container.current==>props.data:', props.data)
      let svg = d3.select(d3Container.current).select('#d3Graph1')
      let rects = svg.selectAll('rect')
        .data(props.data)

      rects
        .enter()
        .append('rect')
        .attr('x', (d, i) => i * 25)
        .attr('width', 20)
      rects.exit().remove()

      rects
        .attr('y', (d)=>{return 100-d})
        .attr('height', d => d)
    }

  },[props.data, d3Container.current])


  return (
      <div className={'barChartD3'}>
        <svg
          className="d3-component"
          width={250}
          height={100}
          ref={d3Container}
        ><g id='d3Graph1'></g></svg>
      </div>
  )

}
export default BarChartD3
