import React, {useEffect, useRef, useState} from 'react'
import {withFauxDOM} from 'react-faux-dom'
import * as d3 from 'd3'
import {makeStyles} from '@material-ui/core/styles'

const styles = makeStyles((theme)=>({
  poobar:{
    marginTop:'20px'
  }
}))

function BarChartD3(props){
  const faux = props.connectFauxDOM('div','chart')
  //let [svg, setSvg] = useState()
  const d3Container = useRef(null);
  const data = [1,2,3,4,5,6,7,8,9]
  useEffect(()=>{
    //let svg = d3.select(faux).append('svg').attr('width', 400).attr('height',300)
    if(d3Container.current){
      console.log('I have d3Container.current==>props.data:', props.data)
      const svg = d3.select(d3Container.current)
      // Bind D3 data
      //const update = svg
        svg.append('g')
        //  .enter()

        .selectAll('rect')
        .data(props.data)

      // Enter new D3 elements
        .enter()
        .append('rect')
        .attr('x', (d, i) => i * 25)
        .attr('y', (d)=>{return 100-d})
        .attr('width', 20)
        .attr('height', d => d)
        .text((d) => d)
          .exit()


    }
    // let g = svg.append('g').attr('id','group1')
    // console.log('props:', props)
    // console.log('g:', g)
    // //d3.select('#group1')
    //   g.append('rect')
    //     .data([1,2,3,4,5,6,7,8,9])
    //   .attr('width', 10)
    //   .attr('height', 10)
    //   .attr('x', (d)=>{return d * 10})
    //   .style('fill','red')




    ///props.animateFauxDOM(800)
  },[data, d3Container.current])


  return (
      <div>
        <svg
          className="d3-component"
          width={400}
          height={200}
          ref={d3Container}
        />
      </div>
  )

}
export default withFauxDOM(BarChartD3)
