import React, {useState, useEffect} from 'react'
import './Component1.css'
import * as rr from './ReactiveRobot'
import * as ev from './Events'

function Component1() {
  const [currentValue, setCurrentValue] = useState([])
  console.log('Component1')

  useEffect(()=>{

    console.log('Component1==>useEffect==>adding my onEvent to rr')
    rr.addObserver(onEvent)

  },[])

  function onEvent(event){
    switch (event.type){
      case ev.TEST_COMPONENT1:
        console.log('Component1==>got test event==>event.data:', event.data)
        setCurrentValue(`${event.data.name} received at ${new Date(Date.now()).toLocaleString()}`)
    }
  }

  // let total = chartValues.reduce((accum,curr)=>{return accum+curr},0)
  // let average = total/chartValues.length
  //let messages = [];

  return (
    <div className="App">
      {'Component1==>currentValue:', currentValue}
    </div>
  );
}

export default Component1;
