import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';
import { createStore } from 'redux'
import {Line, Doughnut} from "react-chartjs-2";
var Chart = require('chart.js');

/**
 * This is a reducer, a pure function with (state, action) => state signature.
 * It describes how an action transforms the state into the next state.
 *
 * The shape of the state is up to you: it can be a primitive, an array, an object,
 * or even an Immutable.js data structure. The only important part is that you should
 * not mutate the state object, but return a new object if the state changes.
 *
 * In this example, we use a `switch` statement and strings, but you can use a helper that
 * follows a different convention (such as function maps) if it makes sense for your
 * project.
 */

let defaultState = {
  chartValues:[]
}
const MAX_CHART_VALS = 10

function App() {

  const [chartValues, updateChartValues] = useState([])
  function reducer(state=defaultState, action) {
    switch (action.type) {
      case 'INCREMENT':
        return state + 1
      case 'DECREMENT':
        return state - 1
      case 'UPDATE_CHART':
        console.log('UPDATE_CHART==>action.value:'+action.value)
        console.log('UPDATE_CHART==>state.chartValues:'+state.chartValues)
        let chartValues = state.chartValues
        chartValues.push(action.value)
        if(chartValues.length > MAX_CHART_VALS){
          chartValues.shift()
        }
        return {...state,chartValues}
      default:
        return state
    }
  }
  let store = createStore(reducer)
  store.subscribe(() => {
    let state = store.getState()
    //console.log('state:', state)
    updateChartValues(state.chartValues)
  })
  setInterval(()=>{
    store.dispatch({type:'UPDATE_CHART', value: Math.random() * 100})
  },1000)
  // The only way to mutate the internal state is to dispatch an action.
  // The actions can be serialized, logged or stored and later replayed.
  // store.dispatch({ type: 'INCREMENT' })
  // // 1
  // store.dispatch({ type: 'INCREMENT' })
  // // 2
  // store.dispatch({ type: 'DECREMENT' })
  // 1
  //console.log('chartValues:', chartValues)

  let data = {
    labels: ["January", "February", "March", "April", "May", "June", "July"],
    datasets: [
      {
        label: "Recruiters contacted per day",
        fillColor: "rgba(220,220,220,0.2)",
        strokeColor: "rgba(220,220,220,1)",
        pointColor: "rgba(220,220,220,1)",
        pointStrokeColor: "#fff",
        pointHighlightFill: "#fff",
        pointHighlightStroke: "rgba(220,220,220,1)",
        data: chartValues
      }
    ]
  };
  // let data =
  //   {
  //     labels:['a','b','c','d'],
  //     dataSets:[{
  //       type:'line',
  //       backgroundColor: "rgba(142, 243, 197, 0.5)",
  //       pointBackgroundColor: "#fff",
  //       pointHoverBackgroundColor: '#fff',
  //       pointStyle: "circle",
  //       label: "Monthly Expenses",
  //       borderColor: "#2be1d8",
  //       borderWidth: 3,
  //       borderJoinStyle: "round",
  //       lineTension: 0.3,
  //       fontColor: "#fff",
  //       hitRadius: 5,
  //       hoverRadius: 8,
  //       radius: 4,
  //       data: chartValues
  //     }]
  //   }
  return (
    <div className="App">
      <Line data={data || {}} />
    </div>
  );
}

export default App;
