import React, {useState, useEffect} from 'react'
import './Component2.css'
import * as rr from './ReactiveRobot'
import * as ev from './Events'

function Component2() {
  const [currentValue, setCurrentValue] = useState(`Component2 inited at ${Date.now()}`)
  console.log('Component2')
  useEffect(()=>{
    rr.addObserver(onEvent)
  },[])

  function onEvent(event){
    switch (event.type){
      case ev.TEST_COMPONENT2:
        setCurrentValue(`${event.data.name} received at ${Date.now()}`)
    }
  }
  return (
    <div className="Component2">
      {'Component2==>:'+ currentValue}
    </div>
  );
}

export default Component2;
