import React, {useState, useEffect} from 'react'
import './Component2.css'
import * as rr from './ReactiveRobot'
import * as ev from './Events'

function Component2() {
  const [currentValue, setCurrentValue] = useState(`Component1 inited at ${new Date(Date.now()).toLocaleString()}`)
  console.log('Component1')
  useEffect(()=>{
    rr.addObserver(onEvent)
  },[])

  function onEvent(event){
    switch (event.type){
      case ev.TEST_COMPONENT1:
        console.log('Component1==>got test event==>event.data:', event.data)
        setCurrentValue(`${event.data.name} received at ${new Date(Date.now()).toLocaleString()}`)
    }
  }
  return (
    <div className="Component2">
      {'Component2==>currentValue:'+ currentValue}
    </div>
  );
}

export default Component2;
