import React, {useState, useEffect} from 'react'
import './Component1.css'
import * as rr from './ReactiveRobot'
import * as ev from './Events'

function Component1(props) {
  const [currentValue, setCurrentValue] = useState(` inited at ${Date.now()}`)
  //const [name, setName] = useState('')
  console.log('Component1==>rendering==>name:', props.name)
  useEffect(()=>{
    rr.addObserver(onEvent)
    console.log(`${props.name} sending ${ev.COMPONENT_UPDATE_GET}`)
    rr.next({type:ev.COMPONENT_UPDATE_GET,data:{name:props.name}})
    return ()=>{rr.removeObserver(onEvent)}//cleanup on unmount
  },[])

  function onEvent(event){
    switch (event.type){

      case ev.COMPONENT1_TEST:
        console.log('component1==>received COMPONENT1_TEST')
        setCurrentValue(`${event.data.name} received at ${Date.now()}`)
        break;
      case ev.COMPONENT_UPDATE_RECEIVED:
        console.log('component1==>received COMPONENT_UPDATE_RECEIVED==>event.data.name:'+event.data.name)
        if(event.data.name===props.name){
          console.log('I got an event for me - I am '+props.name+', event.data.message:', event.data.message)
          setCurrentValue(event.data.message)
        }
    }
  }
  return (
    <div className="Component1">
      <span>{props.name}</span>
      <span>{currentValue}</span>
    </div>
  );
}

export default Component1;
